package electionguard.core

import electionguard.core.Base16.fromHexSafe
import electionguard.core.Base16.toHex
import electionguard.core.Base64.fromBase64Safe
import kotlinx.coroutines.test.TestResult
import kotlin.test.Test
import kotlin.test.assertEquals
import kotlin.test.assertFalse
import kotlin.test.assertTrue

class ConstantsTest {
    @Test
    fun testGroupIsCompatible(): TestResult {
        return runTest {
            val tinyGroup = tinyGroup()
            val productionGroup1 =
                productionGroup(
                    acceleration = PowRadixOption.NO_ACCELERATION,
                    mode = ProductionMode.Mode4096
                )
            val productionGroup2 =
                productionGroup(
                    acceleration = PowRadixOption.LOW_MEMORY_USE,
                    mode = ProductionMode.Mode4096
                )
            val productionGroup3 =
                productionGroup(
                    acceleration = PowRadixOption.LOW_MEMORY_USE,
                    mode = ProductionMode.Mode3072
                )

            assertTrue(tinyGroup.isCompatible(tinyGroup.constants))
            assertFalse(tinyGroup.isCompatible(productionGroup1.constants))

            assertTrue(productionGroup1.isCompatible(productionGroup1.constants))
            assertTrue(productionGroup1.isCompatible(productionGroup2.constants))
            assertFalse(productionGroup1.isCompatible(tinyGroup.constants))

            assertTrue(productionGroup2.isCompatible(productionGroup2.constants))
            assertTrue(productionGroup2.isCompatible(productionGroup1.constants))
            assertTrue(productionGroup2.isCompatible(productionGroup1.constants))
            assertFalse(productionGroup2.isCompatible(productionGroup3.constants))

            assertTrue(productionGroup3.isCompatible(productionGroup3.constants))
            assertFalse(productionGroup3.isCompatible(productionGroup1.constants))
            assertFalse(productionGroup3.isCompatible(productionGroup2.constants))
            assertFalse(productionGroup3.isCompatible(tinyGroup.constants))
        }
    }

    @Test
    fun testConstants(): TestResult {
        return runTest {
            val group = productionGroup()
            val constants = group.constants
            println("smallPrime size= ${constants.smallPrime.size}")
            println("smallPrime     = ${constants.smallPrime.toHex()}")
            println("qnormal        = ${constants.smallPrime.normalize(32).toHex()}")

            val q64 = "AP////////////////////////////////////////9D"
            val q64Bytes = q64.fromBase64Safe()
            println("q64 size= ${q64Bytes.size}")
            println("q64     = ${q64Bytes.toHex()}")

            val q16 = "FFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFF43"
            val q16Bytes = q16.fromHexSafe()
            println("q16 size= ${q16Bytes.size}")
            println("q16     = ${q16Bytes.toHex()}")

            println("\n====================\n")
            println("largePrime size= ${constants.largePrime.size}")
            println("largePrime     = ${constants.largePrime.toHex()}")
            println("pnormal        = ${constants.largePrime.normalize(512).toHex()}")

            val p64 =
                ""
            val p64Bytes = p64.fromBase64Safe()
            println("q64 size= ${p64Bytes.size}")
            println("q64     = ${p64Bytes.toHex()}")

            val p16 =
                ""
            val p16Bytes = p16.fromHexSafe()
            println("p16 size= ${p16Bytes.size}")
            println("p16     = ${p16Bytes.toHex()}")

            assertEquals(32, constants.smallPrime.size)
            assertEquals(512, constants.largePrime.size)
        }
    }
}